//JavaScript

var messaging = CB_PORTAL.ClearBlade.Messaging({useSSL:true}, function(obj){console.log(obj);});

var DEVICE_NAME_SPLIT_STRING = "//";

window.messageTypes = {
    "LED": "01",
    "ADC": "02",
    "Lid": "03"
};

window.pinIDs = {
    "Dark Squid 1": 266,
    "Dark Goat 2": 25,
    "Dark Goat 3": 30
};

window.LEDStates = {
    "Off": "00",
    "On": "01"
};

window.lidStates = {
    "7F": "Error",
    "00": "Closed",
    "81": "Opening",
    "7E": "Open Failed",
    "01": "Open" 
};

window.parseIncomingMessage = function (rawMsg) {
    var splitMsg = rawMsg.split(DEVICE_NAME_SPLIT_STRING);
    if (splitMsg.length !== 2) {
        console.log("Incoming message \"" + rawMsg + "\" has unexpected format");
        return;
    }
    var deviceName = splitMsg[0];
    var msg = splitMsg[1];
    var msgTypeHex = msg.substring(0, 2);
    var msgPayload = msg.substring(2);

    //define helper functions
    function hexToUint16LittleEndian(hexString) {
        return parseInt('0x'+hexString.match(/../g).reverse().join(''));
    }

    function getObjectKeyByValue(object, value) {
        return Object.keys(object).find(key => object[key] === value);
    }

    if (msgTypeHex === "01") {
        var pinIdHex = msgPayload.substring(0, 4);
        var stateHex = msgPayload.substring(4, 6);
        var pin_id = hexToUint16LittleEndian(pinIdHex);
        return {
            "message_type_name": getObjectKeyByValue(window.messageTypes, msgTypeHex),
            "pin_id": pin_id,
            "description": getObjectKeyByValue(window.pinIDs, pin_id),
            "state": getObjectKeyByValue(window.LEDStates, stateHex)
        };
    } else if (msgTypeHex === "02") {
        var pressureHex = msgPayload.substring(0, 4);
        var tempHex = msgPayload.substring(4, 8);
        return {
            "message_type_name": getObjectKeyByValue(window.messageTypes, msgTypeHex),
            "pressure": hexToUint16LittleEndian(pressureHex),
            "temperature": hexToUint16LittleEndian(tempHex)
        };
    } else if (msgTypeHex === "03") {
        var stateHex = msgPayload.substring(4, 6);
        return {
            "message_type_name": getObjectKeyByValue(window.messageTypes, msgTypeHex),
            "state": window.lidStates[stateHex]
        };
    } else {
        console.log("Unexpected Message Type of: " + msgTypeHex);
        return;
    }
};

window.sendCommandMessage = function (msgType, newState, optionalId) {
    function uint16LittleEndianToHex(uint16) {
        var hex = uint16.toString(16);
        hex = ('0000' + hex).slice(-4);     
	    return hex.match(/../g).reverse().join('');
    }

    if (msgType === "LED") {
        var commandMessage = window.messageTypes[msgType];
        commandMessage += uint16LittleEndianToHex(optionalId);
        commandMessage += window.LEDStates[newState];
        commandMessage = commandMessage.toUpperCase();
        messaging.publish("Endpoints/Sensor2", commandMessage);
    } else if (msgType === "Lid") {
        var commandMessage = window.messageTypes[msgType];
        commandMessage += "0000";
        commandMessage += newState;
        commandMessage = commandMessage.toUpperCase();
        messaging.publish("Endpoints/Sensor2", commandMessage);
    } else {
        console.log("Unexpected device type: " + msgType);
        return;
    }
}

//JavaScript
var parsedMessage = window.parseIncomingMessage(this.datasource);
var openLidBtn = document.getElementById("openLidBtn");
var lidState = document.getElementById("lidState");
openLidBtn.onclick = function (evt) {
    evt.target.disabled = true;
    evt.target.innerHTML = "Opening";
    window.sendCommandMessage("Lid", "01");
}
if (parsedMessage && parsedMessage.message_type_name === "Lid") {
    if (parsedMessage.state !== lidState.innerHTML) {
        lidState.innerHTML = parsedMessage.state;
    }
    if (parsedMessage.state === "Closed") {
        openLidBtn.disabled = false;
        openLidBtn.innerHTML = "Open Lid";
    } else {
        openLidBtn.innerHTML = "N/A";
        openLidBtn.disabled = true;
    }
}

