var EXPECTED_PIN_ID = 25;
// todo - should pull this from window.pinIDs object
var led2Name = document.getElementById("led2Name");
led2Name.innerText = "Dark Goat LED 2";
var changeLed2StateBtn = document.getElementById("changeLed2State");
changeLed2StateBtn.onclick = function(evt) {
    evt.target.disabled = true;
    var requestedState = evt.target.getAttribute("requested-state");
    window.sendCommandMessage("LED", requestedState, EXPECTED_PIN_ID);
}
var led2Container = document.getElementById("led2");
if (parsedMessage && parsedMessage.message_type_name === "LED" && parsedMessage.pin_id === EXPECTED_PIN_ID) {
    var existingState = led2Container.getAttribute("current-state");
    if (parsedMessage.state !== existingState) {
        led2Container.classList.remove(existingState + "-state");
        led2Container.classList.add(parsedMessage.state + "-state");
        led2Container.setAttribute("current-state", parsedMessage.state);
        changeLed2StateBtn.disabled = false;
        changeLed2StateBtn.innerHTML = "Turn " + existingState;
        changeLed2StateBtn.setAttribute("requested-state", existingState);
    }
}

